<?php$email = $_POST['email'];$fname = $_POST['first_name'];$lname = $_POST['last_name'];$psswd = $_POST['password'];$pais  = $_POST['pais'];include('conection.php');$query  = "SELECT email FROM usuario WHERE email='$email'";$xquery = $plug->query($query);$flag   = $xquery->num_rows;// if $flag == 0 -> no existe emailif($flag == 0){    $queryI = "INSERT INTO usuario(email,f_name,l_name,psswd,pais)    VALUES('$email','$fname','$lname','$psswd','$pais')";    $xqueryI = $plug->query(utf8_encode($queryI));    if($xqueryI) $mensaje = " Exito!";    else $mensaje = " Fallo el Query ";}else{    $mensaje = "ya existe el correo $email <br>";}$plug->close();header("Location: ../index.php?op=reg&mensaje=$mensaje");?>